// client/src/components/VerifyEmail.jsx
import React, { useEffect, useState } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import API from "../api";

export default function VerifyEmail() {
  const [searchParams] = useSearchParams();
  const token = searchParams.get("token");
  const email = searchParams.get("email");
  const [msg, setMsg] = useState("Verifying...");
  const navigate = useNavigate();

  useEffect(() => {
    if (!token || !email) {
      setMsg("Invalid verification link");
      return;
    }
    const verify = async () => {
      try {
        const res = await API.get(`/auth/verify-email?token=${token}&email=${encodeURIComponent(email)}`);
        setMsg("Email verified. Logging you in...");
        // store token and redirect to dashboard
        localStorage.setItem("token", res.data.token);
        setTimeout(() => navigate("/dashboard"), 1000);
      } catch (err) {
        setMsg(err.response?.data?.message || "Verification failed");
      }
    };
    verify();
  }, [token, email, navigate]);

  return (
    <div className="container mt-5">
      <h3>Email verification</h3>
      <div className="alert alert-info">{msg}</div>
    </div>
  );
}
