// client/src/components/Dashboard.jsx
import React, { useEffect, useState } from "react";
import API from "../api";

export default function Dashboard() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // decode stored token and/or fetch user info
    const token = localStorage.getItem("token");
    if (!token) {
      setUser(null);
      return;
    }
    // attempt to load user company from localStorage placeholder (we set it on login) or call backend later
    // For now, simple display
    setUser({ email: "you@example.com" });
  }, []);

  return (
    <div className="container mt-5">
      <h2>Dashboard</h2>
      <p>Welcome â€” your account is created and verified. Next: upload deck, etc.</p>
      {user && (
        <div className="card p-3">
          <h5>{user.email}</h5>
          <p>Company info will be visible here.</p>
        </div>
      )}
    </div>
  );
}
